function prepareFormData(form){const $form=$(form);const formData=new FormData($form[0]);const multipleElements=$form.find('select[name$="[]"], input[name$="[]"]');multipleElements.each(function(){const name=$(this).attr('name');const values=[];const sameNameElements=$form.find(`[name="${name}"]`);sameNameElements.each(function(){if($(this).val()&&$(this).val().trim()!==''){values.push($(this).val());}});formData.delete(name);values.forEach(function(value){formData.append(name,value);});});const imageInput=$form.find('input[type="file"]')[0];if(imageInput&&imageInput.files&&imageInput.files.length>0){formData.delete(imageInput.name);formData.append(imageInput.name,imageInput.files[0]);}
return formData;}
function submitForm(form,url,successCallback,tableInfo=true){const pageInfo=getFormPageInfo();const formData=prepareFormData(form);if(tableInfo){formData.append('current_page',pageInfo.page);formData.append('search_query',pageInfo.search);}
return fetch(url,{method:'POST',body:formData,headers:{'X-Requested-With':'XMLHttpRequest','X-CSRFToken':document.querySelector('[name=csrfmiddlewaretoken]').value}}).then(async response=>{const data=await response.json();if(!response.ok){throw{response,data};}
return data;}).then(data=>{if(data.status==='success'){if(successCallback){successCallback(data,pageInfo);}
return data;}else{throw new Error(data.message||'エラーが発生しました。');}});};