function initializeSelect2(){try{$('.select2').each(function(){const $element=$(this);if($element.data('select2')){return;}
const $modal=$element.closest('.modal');$element.select2({theme:'bootstrap-5',width:'100%',placeholder:'選択してください',allowClear:true,dropdownParent:$modal.length>0?$modal:$('body')});});}catch(error){console.error('Error initializing select2:',error);}}
function cleanupModalSelect2(){try{$('.modal .select2').each(function(){const $element=$(this);if($element.data('select2')){$element.off('select2:opening select2:open select2:closing select2:close select2:selecting select2:select select2:unselecting select2:unselect');$element.select2('destroy');}});$('.modal .select2-container').remove();$('.select2-container--open').remove();$('.select2-dropdown').remove();$('.select2-hidden-accessible').each(function(){if(!$(this).closest('.modal').length){}});}catch(error){console.error('Error cleaning up modal select2:',error);}}
function initializeRegisterModalSelect2(){try{const $modal=$('#RegisterModal');$('.select2-container--open').remove();$('.select2-dropdown').remove();$modal.find('.select2').each(function(){const $element=$(this);if($element.data('select2')){$element.off('select2:opening select2:open select2:closing select2:close select2:selecting select2:select select2:unselecting select2:unselect');$element.select2('destroy');$element.next('.select2-container').remove();}
$element.select2({theme:'bootstrap-5',width:'100%',placeholder:'選択してください',allowClear:true,dropdownParent:$modal});});$modal.find('.select2').val(null).trigger('change');}catch(error){console.error('Error initializing RegisterModal select2:',error);}};