function clearFormErrors(form){const $form=$(form);const inputs=$form.find('input, select, textarea');inputs.each(function(){$(this).removeClass('is-invalid');});const invalidFeedbacks=$form.find('.invalid-feedback');invalidFeedbacks.each(function(){$(this).remove();});}
function resetRegisterForm(form){const $form=$(form);const inputs=$form.find('input, select, textarea');inputs.each(function(){if($(this).prop('type')==='number'){$(this).val('0');}else if($(this).prop('type')==='text'||$(this).prop('type')==='textarea'){$(this).val('');}else if($(this).prop('tagName')==='SELECT'){if($(this).hasClass('select2')){$(this).val('').trigger('change');}else{if($(this).find('option').length>0){$(this).prop('selectedIndex',0);}}}});}
const modalHandlers={clearFormErrors:function(form){if(!form)return;const $form=$(form);const invalidFeedbacks=$form.find('.invalid-feedback');invalidFeedbacks.each(function(){$(this).remove();});const invalidInputs=$form.find('.is-invalid');invalidInputs.each(function(){$(this).removeClass('is-invalid');});$form.trigger('reset');},resetEditForm:function(form){if(!form)return;const $form=$(form);this.clearFormErrors(form);const inputs=$form.find('input, select, textarea');inputs.each(function(){if($(this).prop('type')==='checkbox'||$(this).prop('type')==='radio'){$(this).prop('checked',false);}else{$(this).val('');}});$form.trigger('reset');}};;