window.initializationFlags=window.initializationFlags||{};function clearInitializationFlags(){window.initializationFlags={};window.tableEventsInitialized=false;}
function debounce(func,wait){let timeout;return function executedFunction(...args){const later=()=>{clearTimeout(timeout);func(...args);};clearTimeout(timeout);timeout=setTimeout(later,wait);};}
function handleFormError(error,form){if(error.response&&error.response.status===400){if(error.data.errors){Object.entries(error.data.errors).forEach(([field,message])=>{const input=form.find(`[name="${field}"]`);if(input.length){input.addClass('is-invalid');const feedback=$('<div>').addClass('invalid-feedback d-block').text(message);input.parent().append(feedback);}});}}else{console.error('Error:',error);showToast('error',error.message||'エラーが発生しました。');}};